myFragOutput fileTexturePluginFragment(float2 uv, texture2D map, sampler2D mapSampler) 
{ 
	myFragOutput result; 
	uv -= floor(uv); 
	uv.y = 1.0f - uv.y; 
	float4 color = tex2D(mapSampler, uv); 
	result.outColor = color.rgb; 
	result.outAlpha = color.a; 
	return result; 
}


myFragOutput fileTexturePluginFragment(float2 uv, Texture2D map, sampler mapSampler) 
{ 
	myFragOutput result; 
	uv -= floor(uv); 
	uv.y = 1.0f - uv.y; 
	float4 color = map.Sample(mapSampler, uv); 
	result.outColor = color.rgb; 
	result.outAlpha = color.a; 
	return result; 
}


myFragOutput fileTexturePluginFragment(vec2 uv, sampler2D mapSampler) 
{ 
	myFragOutput result; 
	uv -= floor(uv); 
	uv.y = 1.0f - uv.y; 
	vec4 color = texture(mapSampler, uv); 
	result.outColor = color.rgb; 
	result.outAlpha = color.a; 
	return result; 
}
