void mx_image_color3(sampler2D tex_sampler, vec2 texcoord, out vec3 result)
{
    if(textureSize(tex_sampler, 0).x > 1)
    {
        result = texture(tex_sampler, texcoord).rgb;
    }
    else
    {
        result = vec3(0.0, 1.0, 0.0);
    }
}

vec3 tiled_image3_function(
	vec2 uv,
   sampler2D mapSampler
)
{
    vec3 tiled_image3_out = vec3(1.0f, 0.0f, 0.0f);
    mx_image_color3(mapSampler, uv, tiled_image3_out);
    return tiled_image3_out;
}
